package io.github.fossure.web.rest.v1;

import io.github.fossure.domain.GenericLicenseUrl;
import io.github.fossure.repository.GenericLicenseUrlCustomRepository;
import io.github.fossure.web.rest.GenericLicenseUrlResource;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

/**
 * Custom REST controller for managing {@link GenericLicenseUrl}
 * where changes are inserted that are different from the base class generated by the JHipster generator.<br>
 * API base path: {@code /api/v1}
 */
@RestController
@RequestMapping("/api/v1")
public class GenericLicenseUrlCustomResource extends GenericLicenseUrlResource {

    public GenericLicenseUrlCustomResource(GenericLicenseUrlCustomRepository genericLicenseUrlRepository) {
        super(genericLicenseUrlRepository);
    }
}
